var pw = 49
function retEl(x,y){
    return y*pw+x;
    }
function retXY(elid){
  var pos=[elid%pw,Math.floor(elid/pw)]
    return pos
}

function repeat(func,times){
    func()
    --times && repeat(func,times)
}

var bdatastr = ""
var boundingbox = ""
var datastr = bdatastr.toString()
var canvas = document.getElementById('draw')
var ctx = canvas.getContext('2d');
var ps = 6;
var mpos = [0,0]

function redraw(){
    var x = 0;
var y = 0;
ctx.beginPath()
ctx.fillStyle='#2D55BE'
ctx.fillRect(0,0,10000,10000)
ctx.stroke();
repeat(function(){
        ctx.beginPath()
        if(datastr.split('')[retEl(x,y)]!=0){
                ctx.fillStyle='#237D1E'
        }else{
            
            if(datastr.split('')[retEl(x,y)]==0){
            ctx.fillStyle='#2D55BE'
            }}
            if(datastr.split('')[retEl(x,y)]==7){
                ctx.fillStyle='#196e15'
            }
        ctx.fillRect(x*ps,y*ps,(x+1)*ps,(y+1)*ps)
        ctx.stroke(); 
         x++
            if(x==49){
            y++
            x=0
            }
},1127);
}
redraw()
window.addEventListener('mousemove',function(event){
mpos = [Math.floor(event.clientX/ps/2-canvas.getBoundingClientRect().left/ps/2),Math.floor(event.clientY/ps/2-canvas.getBoundingClientRect().top/ps/2)]
if(retEl(mpos)>1){
redraw()
}
});

canvas.addEventListener('click',function(){
    datastr=bdatastr.toString()
    if(boundingbox.split('')[retEl(mpos[0],mpos[1])]!=0){
        datastr=datastr.replace(new RegExp(datastr.split('')[retEl(mpos[0],mpos[1])], 'g'), 7);
    }else{
        datastr=bdatastr.toString()
    }
    if(datastr.split('')[1]!=7){
    redraw()
}else{
    datastr=bdatastr
}
});
